
public class GugudanExam1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 조건문 반복문 =>

		for (int i = 2; i <= 9; i++) {
			for (int j = 1; j <= 9; j++) {
				
				System.out.print(i + "*" + j + "=" + i * j + "\t");
				
			}
			System.out.println();
		}
	}

}